mapper.select:
SELECT g.str
FROM user AS u INNER JOIN `group` AS g ON u.groupIdx = g.idx
WHERE u.originId = #{id}

SELECT a.idx AS ino, a.str FROM user AS u INNER JOIN group_authority_group AS gag ON u.groupIdx = gag.groupIdx INNER JOIN authority_group AS ag ON gag.authGroupIdx = ag.idx INNER JOIN authority_group_authority AS aga ON ag.idx = aga.authGroupIdx INNER JOIN authority AS a ON aga.authIdx = a.idx
LEFT JOIN authority_group_override AS ago ON u.idx = ago.userIdx AND ag.idx = ago.authGroupIdx AND ago.status = "deny"
LEFT JOIN authority_override AS ao ON u.idx = ao.userIdx AND a.idx = ao.authIdx AND ao.status = "deny" WHERE u.originId = #{id}
AND ago.userIdx IS NULL
AND ao.userIdx IS NULL
UNION ALL
SELECT a.idx AS ino, a.str FROM user AS u INNER JOIN authority_group_override AS ago ON u.idx = ago.userIdx AND ago.status = "allow" INNER JOIN authority_group AS ag ON ago.authGroupIdx = ag.idx INNER JOIN authority_group_authority AS aga ON ag.idx = aga.authGroupIdx INNER JOIN authority AS a ON aga.authIdx = a.idx LEFT JOIN authority_override AS ao ON u.idx = ao.userIdx AND a.idx = ao.authIdx AND ao.status = "deny" WHERE u.originId = #{id}
AND ao.userIdx IS NULL
UNION ALL
SELECT a.idx AS ino, a.str FROM user AS u INNER JOIN authority_override AS ao ON u.idx = ao.userIdx AND ao.status = "allow" INNER JOIN authority AS a ON ao.authIdx = a.idx WHERE u.originId = #{id}

SELECT ag.idx AS ino, ag.str FROM user AS u INNER JOIN group_authority_group AS gag ON u.groupIdx = gag.groupIdx INNER JOIN authority_group AS ag ON gag.authGroupIdx = ag.idx LEFT JOIN authority_group_override AS ago ON u.idx = ago.userIdx AND ag.idx = ago.authGroupIdx AND ago.status = "deny" WHERE u.originId = #{id} AND ago.userIdx IS NULL
UNION ALL
SELECT ag.idx AS ino, ag.str FROM user AS u INNER JOIN authority_group_override AS ago ON u.idx = ago.userIdx AND ago.status = "allow" INNER JOIN authority_group AS ag ON ago.authGroupIdx = ag.idx WHERE u.originId = #{id}

SELECT id, device, token
FROM remember_me
WHERE id = #{id}
	AND device = #{device}
